import React, { useState } from 'react'



const DisplayHeader = ({text}) => <h1>{text}</h1>

const DisplayStats = (props) => {

console.log(props)
return(<div>{props.text} {props.number}</div>)
}
const Button = (props) => (
  <button onClick={props.handleClick}>
  {props.text}
  </button>
)



const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  const HandleGood = () => setGood(good +1)
  const HandleNeutral = () => setNeutral(neutral +1)
  const HandleBad = () => setBad(bad +1)


  return (
    <div>
      <DisplayHeader text = "Give Feedback"/>
      <Button handleClick={() => HandleGood} text = "Good"/>
      <Button handleClick={() => HandleNeutral} text = "Neutral"/>
      <Button handleClick={() => HandleBad} text = "Bad"/>
      <DisplayHeader text = "Statistics"/>
      <DisplayStats text = "Good" number = {good}/>
      <DisplayStats text = "Neutral" number = {neutral}/>
      <DisplayStats text = "Bad" number = {bad}/>





    </div>
  )
}

export default App
