import React, { useState } from 'react'



const DisplayHeader = ({text}) => <h1>{text}</h1>

const DisplayStats = (props) => <div>{props.text} {props.number}</div>

const Button = (props) => (
  <button onClick={props.handleClick}>
  {props.text}
  </button>
)


const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)



  return (
    <div>
      <DisplayHeader text = "Give Feedback"/>
      <Button handleClick={() => setGood} text = "Good"/>
      <Button handleClick={() => setNeutral} text = "Neutral"/>
      <Button handleClick={() => setBad} text = "Bad"/>
      <DisplayHeader text = "Statistics"/>
      <DisplayStats text = "Good" number = {6}/>



    </div>
  )
}

export default App
